                            Write-Entry -Subtext "Already exists: $backgroundImageoriginal" -Path $global:ScriptRoot\Logs\Scriptlog.log -Color Cyan -log Info
                        }
                    }
                }
                # Now we can start the Season Part
                if ($global:SeasonPosters -eq 'true') {
