                                            $global:AssetTextLang = $global:FANARTAssetTextLang
                                            $PosterUnknownCount++
                                            if ($global:FavProvider -ne 'FANART') {
                                                $global:IsFallback = $true
                                            }
                                        }
                                        elseif ($global:posterurl -like 'https://artworks.thetvdb.com*') {
